    private void loginBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginBtnActionPerformed
        String user = userTxt.getText();
        String pass = passTxt.getText();
        LoginControlador login = new LoginControlador(user,pass);
        int res = login.getEstado();
        if(res<0) JOptionPane.showMessageDialog(this, "Datos incorrectos");
        else{
            MainWindow ventana = new MainWindow(res);
            ventana.setResizable(false);
            ventana.setDefaultCloseOperation(JFrame.HIDE_ON_CLOSE);
            ventana.setVisible(true);
        }
        
    }//GEN-LAST:event_loginBtnActionPerformed

    private void guessBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_guessBtnActionPerformed
        MainWindow ventana = new MainWindow(-1);
        ventana.setResizable(false);
        ventana.setDefaultCloseOperation(JFrame.HIDE_ON_CLOSE);
        ventana.setVisible(true);
    }//GEN-LAST:event_guessBtnActionPerformed

